#include "textflag.h"

// Called by C code generated by cmd/cgo.
// func crosscall2(fn func(a unsafe.Pointer, n int32, ctxt uintptr), a unsafe.Pointer, n int32, ctxt uintptr)
// Saves C callee-saved registers and calls fn with three arguments.
TEXT crosscall2(SB),NOSPLIT|NOFRAME,$0
	/*
	 * We still need to save all callee save register as before, and then
	 *  push 3 args for fn (R1, R2, R3).
	 * Also note that at procedure entry in gc world, 16(RSP) will be the
	 *  first arg.
	 * TODO(minux): use LDP/STP here if it matters.
	 */
	SUB	$(8*26), RSP
	// +------------+
	// | R19 - R28  |
	// +------------+
	// | R29, R30   |
	// +------------+
	// | F8 - F15   |
	// +------------+
	// | R1, R2, R3 |
	// +------------+
	// | FP, LR     |
	// +------------+
	MOVD	R1, (8*2)(RSP)
	MOVD	R2, (8*3)(RSP)
	MOVD	R3, (8*4)(RSP)
	MOVD	R19, (8*5)(RSP)
	MOVD	R20, (8*6)(RSP)
	MOVD	R21, (8*7)(RSP)
	MOVD	R22, (8*8)(RSP)
	MOVD	R23, (8*9)(RSP)
	MOVD	R24, (8*10)(RSP)
	MOVD	R25, (8*11)(RSP)
	MOVD	R26, (8*12)(RSP)
	MOVD	R27, (8*13)(RSP)
	MOVD	g, (8*14)(RSP)
	MOVD	R29, (8*15)(RSP)
	MOVD	R30, (8*16)(RSP)
	FMOVD	F8, (8*17)(RSP)
	FMOVD	F9, (8*18)(RSP)
	FMOVD	F10, (8*19)(RSP)
	FMOVD	F11, (8*20)(RSP)
	FMOVD	F12, (8*21)(RSP)
	FMOVD	F13, (8*22)(RSP)
	FMOVD	F14, (8*23)(RSP)
	FMOVD	F15, (8*24)(RSP)

	MOVD	R0, R19

	// Initialize Go ABI environment
	BL	runtimeÂ·load_g(SB)
	BL	(R19)

	MOVD	(8*5)(RSP), R19
	MOVD	(8*6)(RSP), R20
	MOVD	(8*7)(RSP), R21
	MOVD	(8*8)(RSP), R22
	MOVD	(8*9)(RSP), R23
	MOVD	(8*10)(RSP), R24
	MOVD	(8*11)(RSP), R25
	MOVD	(8*12)(RSP), R26
	MOVD	(8*13)(RSP), R27
	MOVD	(8*14)(RSP), g
	MOVD	(8*15)(RSP), R29
	MOVD	(8*16)(RSP), R30
	FMOVD	(8*17)(RSP), F8
	FMOVD	(8*18)(RSP), F9
	FMOVD	(8*19)(RSP), F10
	FMOVD	(8*20)(RSP), F11
	FMOVD	(8*21)(RSP), F12
	FMOVD	(8*22)(RSP), F13
	FMOVD	(8*23)(RSP), F14
	FMOVD	(8*24)(RSP), F15
	ADD	$(8*26), RSP
	RET
